module.exports = Bool;

function Bool(bool) {
    this._value = bool;
    this.valueOf = function () {
        return this._value;
    };
    this.encoding = function () {
        var result = this._value ? "01" : "00";
        for (var i = 0; i < 31; ++i) {
            result.unshift("00");
        }
        return result;
    };
    this.toString = function () {
        return this._value ? "true" : "false" ;
    };
}
