module.exports = Enum;

function Enum(hexString, names) {
    var nameList = []
    for (var name in names) {
        nameList[names[name]] = name;
    }
    
    this._value = parseInt(hexString, 16);
    this.valueOf = function () { return this._value; };
    this.encoding = function () {
        var result = this._value.toString(16);
        while (result.length < 32*2) {
            result = "00" + result;
        }
        return result;
    }
    this.toString = function () { return nameList[this._value]; };
}
